// alten wert aus dem kontext laden
var yold = context.get('y') || 0;

// zufallswert addieren
var factor = 1.0 / flow.get("inputscale");
var ynew = yold + (Math.random() - 0.5) * factor;

// intervall beachten!
ynew = Math.max(0, ynew);
ynew = Math.min(1, ynew);

// neuen wert speichen
context.set('y', ynew);

msg.payload = ynew;
return msg;

