const auth = global.get("auth") || {};

//See if auth has a property  (msg.payload.username
if (!auth.hasOwnProperty(msg.payload.username)) {
    msg.payload = `"${msg.payload.username}" not found!`
    return [null, msg];
}

//get password from auth object
let expectedPW = auth[msg.payload.username];
if (msg.payload.password == expectedPW) {
    msg.payload = `Welcome Back, ${msg.payload.username}!`;
    return [msg, null];
} else {
    msg.payload = `Wrong Pasword for "${msg.payload.username}". Please try again`
    return [null, msg];
}

